pub enum Event<T: Config> {
    ExtrinsicSuccess {
            dispatch_info: DispatchInfo,
        },
    ExtrinsicFailed {
            dispatch_error: DispatchError,
            dispatch_info: DispatchInfo,
        },
    CodeUpdated,
    NewAccount {
            account: T::AccountId,
        },
    KilledAccount {
            account: T::AccountId,
        },
    Remarked {
            sender: T::AccountId,
            hash: T::Hash,
        },
    __Ignore(frame_support::sp_std::marker::PhantomData<T>,
            frame_support::Never),
}

pub enum Call<T: Config> {
    __Ignore(frame_support::sp_std::marker::PhantomData<(T,)>,
            frame_support::Never),
    fill_block {
            ratio: Perbill,
        },
    remark {
            remark: Vec<u8>,
        },
    set_heap_pages {
            pages: u64,
        },
    set_code {
            code: Vec<u8>,
        },
    set_code_without_checks {
            code: Vec<u8>,
        },
    set_storage {
            items: Vec<KeyValue>,
        },
    kill_storage {
            keys: Vec<Key>,
        },
    kill_prefix {
            prefix: Key,
            subkeys: u32,
        },
    remark_with_event {
            remark: Vec<u8>,
        },
}
